import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Form, Button, Row, Col, Alert, Container, Image } from "react-bootstrap";
import { userLoginAction } from "../actions/loginAction";
import axios from "axios";

const LoginScreen = ({ history }) => {
  const [num, setNum] = useState("");
  const [uName, setUname] = useState("")
  const [uMail, setUmail] = useState("")
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { success, userNumber, error } = userLogin;
  const submitHandler = async (e) => {
    e.preventDefault();
    dispatch(userLoginAction(num));
  };

  useEffect(() => {
    if (success) {
      history.push("/verify");
    }
  }, [success, history]);

  return (
    <div>
      {/* <h2 className="home-space">Login</h2> */}
      {success && (
        <Alert className="text-center" variant="success">
          ✔️✔️ otp sent successfully
        </Alert>
      )}
      {error && (
        <Alert className="text-center" variant="danger">
          {error}
        </Alert>
      )}
      <Image src="/images/vybeslogo.jpg" width={100} height={100} rounded className="mt-3"/>
      <div className="border d-flex align-items-center justify-content-center box">
      <Form className="p-3">
        <Form.Group className="mb-3" controlId="formBasicUserName">
        <Form.Label>User Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter user name"
                onChange={(e) => setUname(e.target.value)}
              />
        </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Mobile Number</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter your mobile number"
                onChange={(e) => setNum(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your mail-id"
                onChange={(e) => setUmail(e.target.value)}
              />
              <Form.Text className="text-muted">
                We'll never share your info with anyone else.
              </Form.Text>
        </Form.Group>
        <Button  onClick={(e) => submitHandler(e)}>lets go🚀</Button>
      </Form>
      </div>
    </div>
  );
};

export default LoginScreen;
